package com.example.continent_country;

import android.os.Bundle;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.continent_country.databinding.FragmentContinentBinding;

import java.util.ArrayList;


public class ContinentFragment extends Fragment implements onClick {
    private FragmentContinentBinding binding;
    private final ArrayList<ContinentModel> continentList = new ArrayList<>();
    private ContinentAdapter adapter;


    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentContinentBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        adapter = new ContinentAdapter(continentList, this::onClick);
        binding.rvContinent.setAdapter(adapter);
    }

    private void loadData() {
        continentList.add(new ContinentModel("Asia", "https://upload.wikimedia.org/wikipedia/commons/thumb/b/bd/Asia_%28orthographic_projection%29_without_New_Guinea.svg/1200px-Asia_%28orthographic_projection%29_without_New_Guinea.svg.png"));
        continentList.add(new ContinentModel("Europe", "https://upload.wikimedia.org/wikipedia/commons/thumb/d/de/Europe_orthographic_Caucasus_Urals_boundary.svg/220px-Europe_orthographic_Caucasus_Urals_boundary.svg.png"));
        continentList.add(new ContinentModel("North America", "https://upload.wikimedia.org/wikipedia/commons/thumb/4/43/Location_North_America.svg/1200px-Location_North_America.svg.png"));
        continentList.add(new ContinentModel("South America", "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0f/South_America_%28orthographic_projection%29.svg/330px-South_America_%28orthographic_projection%29.svg.png"));
        continentList.add(new ContinentModel("Africa", "data:image/jpeg;base64,"));
        continentList.add(new ContinentModel("Australia", "data:image/jpeg;base64,"));
    }

    @Override
    public void onClick(int position) {
        Bundle bundle = new Bundle();
        bundle.putInt("Key", position);
        CountryFragment countryFragment = new CountryFragment();
        countryFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.cont_Frame, countryFragment).addToBackStack(null).commit();
    }
}